require(['jquery', 'superLabel', 'owlcarousel', 'masonry'],
    function ($) {
        function header_sticky() {
            var header_height = $('.page-header').height(); if ($('body').hasClass('cms-index-index')) {
                if ($(window).scrollTop() >= header_height) {
                    $('.page-header').addClass('sticky-header'); $('body').addClass('header-is-sticky'); setTimeout(function () { $('.page-header').addClass('sticked'); }, 600);
                } else {
                    $('.page-header').removeClass('sticked sticky-header'); $('body').removeClass('header-is-sticky');
                }
            } else { $('.page-wrapper').attr('style', 'padding-top: ' + header_height + 'px'); }
        }
        $(window).scroll(function () { header_sticky(); }); $(document).ready(function () { header_sticky(); }); $('.product-slides').owlCarousel({ items: 5, loop: false, rewind: true, responsiveClass: true, margin: 10, nav: true, responsive: { 0: { items: 2 }, 360: { items: 3 }, 768: { items: 4 }, 1024: { items: 5 } } }); $('.open_form_review').click(function () { $(this).next('.review-add').toggle(); }); $('.question-line .title').click(function () { $(this).toggleClass('active'); $(this).next('.content').toggle(); });
         $('.megamenu-banner-slider .owl-carousel').owlCarousel({ items: 1, responsiveClass: true, nav: false, dots: true, loop: true, margin: 0, autoplay: true, autoplayTimeout: 3000, autoplayHoverPause: true }); $('.ves-vertical-left .btn-menu-vertical').click(function () { $(this).next('.magemenu-menu').toggleClass('active'); }); const debounce = (fn) => {
            let frame; return (...params) => {
                if (frame) { cancelAnimationFrame(frame); }
                frame = requestAnimationFrame(() => { fn(...params); });
            }
        }; const storeScroll = () => { document.documentElement.dataset.scroll = window.scrollY; }
        document.addEventListener('scroll', debounce(storeScroll), { passive: true }); storeScroll();
    });



